            if(ch!='+'&&ch!='-'&&ch!='=')
            {
                if(s[0]!=ch){
                    pp++;
                    if(pp==1){    
                        s[0]=ch;
                        if(p==0)now1+=x*(-1)*f,f=-1,x=0;
                        else if(p==1) now1+=x*(-1)*f,f=1,x=0;
                    }
                    if(pp==2){
                        now2+=x*f;
                        break;
                    }
                }
                else if(s[0]==ch){
                    if(p==0)now1+=x*(-1)*f,f=-1,x=0;
                    else if(p==1) now1+=x*(-1)*f,f=1,x=0;
                }
            }
            else if(ch=='-'){
                now2+=x*f;
                x=0;
                f=f*(-1);
            }
            else if(ch=='='){
                now2+=x*f;
                x=0;
                p=1;
                f=1;
            }
            else if(ch=='+'){
                now2+=x*f;
                if(p==0) f=-1;
                else f=1;
                x=0;
            }
        }
        else if(ch<='9'&&ch>='0'){
            x=x*10+ch-'0';
        }
        if(pp==2) break;
    }
    printf("%c=%.3f",s[0],double(now2)/double(now1));
}
