```cpp
#include <bits/stdc++.h>
#define cd complex<double>
using namespace std;
typedef long long ll;
int main()
{
	double a1,b1,c1,d1;
	scanf("%lf %lf %lf %lf",&a1,&b1,&c1,&d1);
	cd a(a1,0),b(b1,0),c(c1,0),d(d1,0);
	cd k,m,n;
	cd p,q;
	cd y1,y2,y3;
	cd x1,x2,x3;
	cd delta;
	cd w;
	cd i(0,1.0);
	w=(-1.0+sqrt(3)*i)/2.0;
	k=b/a;
	m=c/a;
	n=d/a;
	cd xy=k/3.0;
	p=m-(k*k)/3.0;
	q=(2.0*k*k*k)/27.0-(k*m)/3.0+n;
	delta=(q/2.0)*(q/2.0)+(p/3.0)*(p/3.0)*(p/3.0);
	cd t1,t2;
	t1=(-q)/2.0+pow(delta,1/2.0);
	t2=(-q)/2.0-pow(delta,1/2.0);
	y1=pow(t1,1/3.0)+pow(t2,1/3.0);
	y2=w*pow(t1,1/3.0)+w*w*pow(t2,1/3.0);
	y3=w*w*pow(t1,1/3.0)+w*pow(t2,1/3.0);
	x1=y1-xy;
	x2=y2-xy;
	x3=y3-xy;
	double xa,xb,xc;
	xa=x1.real();
	xb=x2.real();
	xc=x3.real();
    //别忘了排序
	if(xa>xb) swap(xa,xb);
	if(xa>xc) swap(xa,xc);
	if(xb>xc) swap(xb,xc);
	printf("%.2lf %.2lf %.2lf",xa,xb,xc);
    return 0;
}

