```cpp
#include<iostream>
using namespace std;
int n,k,sum=0;
int flag[10001];
void dfs(int temp,int t,int remain) //temp表示前一个函数选用的数 t表示当前选第几个数 remain表示剩余多少 
{
	if(t>k) //剪枝，必须要 
	{
		return ;
	}
	for(int i=temp;i<=remain-(k-t);i++) //剪枝，从上一个数循环到当前能取到的最大值，也就是从remain里抠掉剩下的几个数 
	{
		if(t==k&&(remain-i)==0) //如果符合条件，已经分完 
		{
			sum++; //个数加一 
			return; //一个小小的剪枝 
		}
		else
		{
			dfs(i,t+1,remain-i); //否则继续分割 
		}
	}
}
int main()
{
	cin>>n>>k; //输入，变量依原题 
	dfs(1,1,n); //开始愉快的爆搜 
	cout<<sum<<endl; //输出
	return 0; //完美の结束 
 } 
```

