int n,a[105],sum=0,ave,ans=0;
int main()
{
  cin>>n;
  for(int i=1; i<=n; i++) { cin>>a[i]; sum+=a[i]; } //读入数据，计算总和
  ave=sum/n; //计算平均值
  for(int i=1; i<=n-1; i++)
  {
    if(a[i]==ave) continue; //如果已经等于平均值，就跳过不处理。
    ans++; //否则需要处理，答案+1
    if(a[i]>ave) //大于平均值
    {
      a[i+1]=a[i+1]+(a[i]-ave); //将多余牌放到第i+1堆上
      a[i]=ave; //当前牌堆数变为平均值
    }
    if(a[i]<ave) //小于平均值
    {
      a[i+1]=a[i+1]-(ave-a[i]); //这里不解释了，同上
      a[i]=ave;
    }
  }
  cout<<ans<<endl; //最后输出最少移动次数
  return 0;
} //结束
