bool isPrime(int n)
{
    for(int i=2; i<n; i++)
    {
        if(n%i==0)
        {
            return 0;
        }
    }
    return 1;
}

int num[22]={0};
int n,k,ans=0;

int dfs(int i,int nums,int sum){
    if(nums==k){
        if(isPrime(sum)){
            ans++;
        }
    }
    else if(i<=n){
        dfs(i+1,nums,sum);
        dfs(i+1,nums+1,sum+num[i]);
    }

}

int main()
{
