void pri(int end){
    prime[0]=prime[1]=1;
    for(int i=2;i*i<=end;i++){
        if(!prime[i]){
            for(int j=i*i;j<=end;j+=i)
                prime[j]=1;
        }
    }
}
//dfs与回溯------------------------------
void search(int gs,int ans,int mx){
    if(gs==k){
        if(!prime[ans]){
            rans++;
//            printf("%d\n",ans);
        }    
        return;
    }
    for(int i=mx;i<=n;i++){
        if(!book[i]){
            book[i]=1;
            search(gs+1,ans+a[i],i+1);
            book[i]=0;
        }
    }
}
int main(){
    long long max=0;
    scanf("%d%d",&n,&k);
    for(int i=1;i<=n;i++){
        scanf("%d",&a[i]);
        max+=a[i];//求出最大可能的素数
    }
    pri(max);
    search(0,0,1);
    printf("%d",rans);
    return 0;
}
