    for(i=2;i*i<=n;i++)if(n%i==0)return 0;
    return 1;
}
void dfs(int k,int i,int s){
    if(!k){ans+=zs(s);return;}
    for(i;i<=n;i++)dfs(k-1,i+1,s+a[i]);
}
int main(){
    int i;
    scanf("%d%d",&n,&m);
    for(i=1;i<=n;i++)scanf("%d",&a[i]);
    dfs(m,1,0);
    printf("%d\n",ans);
    return 0;
}

