```cpp
#include <iostream>
#include <cmath>
#include <algorithm>
#include <cstring>
#include <cstdio>

using namespace std;

int n,m,a[1000000],jishu;

int sushu(int x)//判断素数
{
	for(int i=2;i<=sqrt(x);i++)
	{
		if(x%i==0)
		return 0;
	}
	return 1;
}

void DFS(int x,int y,int z)
{
/*
X表示当前需要确定第X个数是否选
y表示之前选了多少个数
z表示之前选的数的和
*/
	if(x>n)//判断是否已经确定所有数是否选。
	{
		if(y==m)
		{
			jishu=jishu+sushu(z);
            //如果恰好选了y个数，就计算这个数是否为素数
		}
		return ;
	}
	DFS(x+1,y+1,z+a[x]);//搜索选择数X的情况
	DFS(x+1,y,z);//搜索不选择数X的情况
}
int main()
{
    cin>>n>>m;//读入
    for(int i=1;i<=n;i++)  cin>>a[i];
    DFS(1,0,0);//从1开始
    cout<<jishu<<endl;//输出


	return 0;
}


