```cpp
#include <cstdio>
#include <cstdlib>
#include <algorithm>
using namespace std;
const int N=31;
int n,d[N],root[N][N];
long long f[N][N];
void put(int l,int r){
	if(l>r)
		return ;
	if(l==r)
		return printf("%d ",l),void(0);
	printf("%d ",root[l][r]);
	put(l,root[l][r]-1);
	put(root[l][r]+1,r);
}
int main(){
	scanf("%d",&n);
	for(int i=1;i<=n;i++)
		scanf("%d",&d[i]);
	for(int j=0;j<n;j++)
		for(int i=1;i+j<=n;i++){
			if(!j){
				f[i][i]=d[i];
				root[i][i]=i;
				continue;
			}
			long long res;
			if(f[i+1][i+j]+d[i]>=f[i][i+j-1]+d[i+j]){
				res=f[i+1][i+j]+d[i];
				root[i][i+j]=i;
			}
			else{
				res=f[i][i+j-1]+d[i+j];
				root[i][i+j]=j;
			}
			for(int k=i+1;k<i+j;k++)
				if(f[i][k-1]*f[k+1][i+j]+d[k]>res){
					res=f[i][k-1]*f[k+1][i+j]+d[k];
					root[i][i+j]=k;
				}
			f[i][i+j]=res;
		}
	printf("%lld\n",f[1][n]);
	put(1,n);
	return 0;
}
