        for(int k = 1; k < n; k++) {
            for(int i =1 ; i + k <= n; i++) {
                for(int j = i; j <= i + k; j++) {
                    int s = f[i][j-1] * f[j+1][i+k] + f[j][j];
                    if(s > f[i][i+k]) {
                        f[i][i+k] = s;
                        root[i][i+k] = j;
                    }
                }
            }
