vector<int> path;


void getPath(int begin, int end) {
    int index;
    if (begin > end) {
        return;
    }

    for (int i = begin; i <= end; i++) {
        if (grades[i] + mat[begin][i - 1] * mat[i + 1][end] == mat[begin][end]) {
            index = i;
            break;
        }
    }
    if (begin == end) {
        index = begin;
    }

    path.push_back(index);
    getPath(begin, index - 1);
    getPath(index + 1, end);
}

int main() {
