```pascal
function dp(l,r:longint):int64;
var
 q,tmp:int64;
 i:longint;
begin
 if l>r then exit(1);
 if l=r then
 begin
  root[l,r]:=l;
  exit(a[l]);
 end;
 //边界
 if f[l,r]>-1 then exit(f[l,r]);//记忆化
 q:=-1;
 for i:=l to r do
 begin
  tmp:=dp(l,i-1)*dp(i+1,r)+a[i];//计算加分
  if tmp>q then//选择最优解
  begin
   q:=tmp;
   root[l,r]:=i;
  end;
 end;
 f[l,r]:=q;
 exit(q);
end;

