    cout<<r[i][j]<<' ';
//    printf("%d ",r[i][j]);
    if(i==j)return;
    if(r[i][j]!=i)p(i,r[i][j]-1);
    if(r[i][j]!=j)p(r[i][j]+1,j);
    return;
}
int main(){
    int n;
//    scanf("%d",&n);
    cin>>n;
    for(int i=1;i<=n;i++){
//        scanf("%d",&sc[i]);
        cin>>sc[i];
        f[i][i]=sc[i];
        r[i][i]=i;
    }
    for(int i=1;i<n;i++){
        f[i][i+1]=sc[i]+sc[i+1];
        r[i][i+1]=i;
    }
    for(int len=3;len<=n;len++){
        for(int i=1;i+len-1<=n;i++){
            int j=i+len-1;
            if(f[i][j-1]+sc[j]>f[i+1][j]+sc[i]){
                f[i][j]=f[i][j-1]+sc[j];
                r[i][j]=j;
            }
            else{
                f[i][j]=f[i+1][j]+sc[i];
                r[i][j]=i;
            }
            for(int k=i+1;k<j;k++){
                if(f[i][k-1]*f[k+1][j]+sc[k]>f[i][j]){
                    f[i][j]=f[i][k-1]*f[k+1][j]+sc[k];
                    r[i][j]=k;
                }
            }
        }
    }
//    printf("%lld\n",f[1][n]);
    cout<<f[1][n]<<endl;
    p(1,n);
    return 0;
}
