#include<cstdio>
using namespace std;
int mid[35],n;
int dp[35][35],pre[35][35];
//dp[i][j]:由i~j结点构成的子树的最大加分
//pre[i][j]:由i~j结点构成的子树的根结点 
void print(int lt,int rt)
{
    if(lt>rt)return;
    printf("%d ",pre[lt][rt]);
    if(lt==rt)return;//叶结点 
    print(lt,pre[lt][rt]-1);
    print(pre[lt][rt]+1,rt);
}
int main(void)
{
    scanf("%d",&n);
    for(int i=1;i<=n;i++)scanf("%d",mid+i);
    for(int i=1;i<=n;i++)
        dp[i][i]=mid[i],pre[i][i]=i;
    //处理叶子结点 
    for(int len=1;len<=n;len++)
    {//子树结点数从小到大 
        for(int i=1;i+len<=n;i++)
        {//子树最左端的结点 
            int j=len+i;//子树最右端的结点 
            int x=mid[j]+dp[i][j-1];
            //j加上仅一棵子树的情况 
            dp[i][j]=mid[i]+dp[i+1][j];
            pre[i][j]=i;//i加上仅一棵子树的情况 
            if(dp[i][j]<x)
                dp[i][j]=x,pre[i][j]=j; 
            for(int k=i+1;k<j;k++)
            {//两棵子树的情况(k是根结点) 
                x=mid[k]+dp[i][k-1]*dp[k+1][j];
                if(dp[i][j]<x)
                    dp[i][j]=x,pre[i][j]=k;
            } 
        }
    }
    printf("%d\n",dp[1][n]);
    print(1,n);
    return 0;
}
