``` cpp
#include <cstdio>
#include <stack>
#include <cstring>
#include <algorithm>
using namespace std;
const int N = 33;

long long f[N][N]; // 好像会爆long long
int root[N][N];

struct DFS {
    int l, r;
};
stack <DFS> stk; // 手动模拟递归
int main() {
    int n;
    scanf("%d", &n);
    for (int i = 1; i <= n; i++)
        scanf("%lld", &f[i][i]), root[i][i] = i;

    for (int i = 1; i <= n; i++)
        f[i][i - 1] = 1ll;

    for (int i = n; i >= 1; i--) {
        for (int j = i + 1; j <= n; j++) {
            for (int k = i; k <= j; k++) {
                long long a = f[i][k - 1] * f[k + 1][j] + f[k][k];       // 因为是中序遍历, 所以对于比i点编号小的点一定是它的左子树, 同理比i点大的点是它的右子树
                long long &b = f[i][j];
                if (a > b) {
                    f[i][j] = a;
                    root[i][j] = k;
                } else if (a == b && k < root[i][j]) {
                    root[i][j] = k;
                }
            }
        }
    }
    stk.push((DFS) {1, n});
    printf("%lld\n", f[1][n]);

    while (!stk.empty()) {                                      // 非递归先序遍历, 好吧主要是题太简单了, 加点难度
        DFS x = stk.top(); stk.pop();
        int pos = root[x.l][x.r];
        printf("%d ", pos);
        if (pos < x.r) stk.push((DFS) {pos + 1, x.r});
        if (pos > x.l) stk.push((DFS) {x.l, pos - 1});
    }
    return 0;
}
