    char s;//记录每次输入的字符 
    int k1=0,k2=0,l1=0,l2=0;//统计分值
    int i=0,j=0;//统计局数 
    while(cin>>s){//每次输入一个字符 
        if(s=='E') {//输入E则跳出循环并记录目前的分值 
            a1[i+1]=k1,a2[i+1]=k2,b1[j+1]=l1,b2[j+1]=l2;
            break;
