        if(s=='W') k1++,l1++;//输入W则华华加分，输入L则对手加分 
        if(s=='L') k2++,l2++;
        if((k1>=11&&abs(k1-k2)>=2)||(k2>=11&&abs(k1-k2)>=2)) {
            i++;//abs是绝对值函数，这里用来让分差大于等于2 
            a1[i]=k1,a2[i]=k2;
