        }
        if((l1>=21&&abs(l1-l2)>=2)||(l2>=21&&abs(l1-l2)>=2)){
            j++;
            b1[j]=l1,b2[j]=l2;
            l1=0,l2=0;//这里统计21分制的 
        }	
    }
    for(int c=1;c<i+2;c++)//输出11分制的答案 
