void judge(int m){
    int a=0,b=0,i;
    for (i=0; i<len; i++) {
        if (s[i]=='W') {
            a++;
        }
        if (s[i]=='L') {
            b++;
        }
        if ((a>=m && a-b>=2)||(b>=m && b-a>=2)) {
            cout<<a<<':'<<b<<endl;
            a=b=0;
        }
    }
    if (!a&&!b || i==len) {
        cout<<a<<':'<<b<<endl;
    }
}
int main()
{
    char t;
    while (cin>>t,t!='E') {
        s[len++]=t;
    }
    judge(11);
    cout<<endl;
    judge(21);
    return 0;
}

