#include<string.h>

struct node{
	int fscore[2];
	node *next;
};

char info[26];
using namespace std;
int score[2][2] = {0};
node *head[2] = {NULL}, *tail[2] = {NULL};
int total[2]={11,21};

void win(int k,int state)
{
	if((((score[k][0] >= total[k]) || (score[k][1] >= total[k])) && (abs(score[k][0]-score[k][1]) >= 2)) || (state == 1)){
		node *temp = NULL;
		temp = new node;
		temp->fscore[0] = score[k][0];
		temp->fscore[1] = score[k][1];
		score[k][0] = score[k][1] = 0;

		if(head[k] == NULL){
			head[k] = tail[k] = temp;
			temp->next = NULL;
		}
		else{
			tail[k]->next = temp;
			temp->next = NULL;
			tail[k] = temp;
		}
	}
}

void print(int k)
{
	node *curNode = head[k];
	while(curNode){
		printf("%d:%d\n",curNode->fscore[0],curNode->fscore[1]);
		curNode = curNode->next;
	}
}

