    scanf("%d%d",&n,&m);
    for (int i=1; i<=n; i++)
    {
        scanf("%d",&num[i]);
        num[i+n]=num[i];    
    }
    for (int i=0; i<n; i++) dp(num+i); 
    printf("%d\n%d\n",Min,Max);
