 if f[x,y,k]=-p then
 begin
  maxi:=min(y-k+2,y-1);
  for i:=x to maxi do
   f[x,y,k]:=max(f[x,y,k],try_max(x,i,1)*try_max(i+1,y,k-1));
 end;
 exit(f[x,y,k]);
end;

function try_min(x,y,k:longint):longint;
var i,maxi:longint;
begin
 if f[x,y,k]=p then
 begin
  maxi:=min(y-k+2,y-1);
  for i:=x to maxi do
   f[x,y,k]:=min(f[x,y,k],try_min(x,i,1)*try_min(i+1,y,k-1));
 end;
 exit(f[x,y,k]);
end;

