                     //更新最小值或最大值（请提交时删掉）
				 	 if(i==n)\
				 	 	 printf("%d\n",ans);\
				 }
#define Is(ch) ((ch>='0' && ch<='9') || ch=='-')
int dp[2*50+5][2*50+5][9+5];
int s[2*50+5],x[50+5],n,m,e;
inline int Read(){//快读
	int ret=0,fs=0;
	char ch=getchar();
	while(!Is(ch))
		ch=getchar();
	fs=(ch=='-'?-1:1);
	if(ch=='-')
		ch=getchar();
	while(Is(ch)){
		ret=f(ret)+C(ch);
		ch=getchar();
	}
	return ret*fs;
}
inline int dfs(int l,int r,int h){
	if(h==1)//分成自已的一组。
		return F(l,r);//区间%10的值。
	if(dp[l][r][h]==-1)//没有计算过。
		for(int i=l;i<=r-1;i++)
			for(int j=1;j<=h-1;j++)
				if((i-l+1)>=j && r-i>=h-j)
					Minax(dp[l][r][h],dfs(l,i,j)*dfs(i+1,r,h-j));
	return dp[l][r][h];
}
int main(){
	n=Read();
	m=Read();
	for(int i=1;i<=n;i++)
		s[i]=s[i-1]+(x[i]=Read());
	for(int i=n+1;i<=2*n;i++)
		s[i]=s[i-1]+x[i-n];//赋值一遍数组。
	print(1);
	print(0);
	return 0;	
}
