//高精/打表：
#include<iostream>
#include<cstdio> 
#include<cstring>
#define MAX_N 110
using namespace std;
int f[MAX_N][MAX_N],c[MAX_N];
inline int len(int a[]) 
{
    int i;
    for(i=60;i>=0;i--)//想要100个以上，这个i的范围要改 
    {
    	if(a[i]!=0) break;
	}   
    return i;
}
inline void add(int a[],int b[],int w)//高精加法 
{
    int lena=len(a),lenb=len(b);
    for(int i=0;i<=max(lena,lenb);i++)
    {
    	f[w][i]=a[i]+b[i];
	}
    for(int i=0;i<=max(lena,lenb)+1;i++) 
	{
        f[w][i+1]+=f[w][i]/10;
        f[w][i]%=10;
    }
}
inline void Catalan(int a[],int b[])//卡特兰 
{
    memset(c, 0, sizeof(c));
	int lena=len(a),lenb=len(b);
    for (int i=0;i<=lena;i++){
    	for (int j=0;j<=lenb;j++) 
    		c[i+j]+=a[i]*b[j];
	}      
    for (int i=0;i<=lena+lenb+1;i++)
	{
        c[i+1]+=c[i]/10;
        c[i]%=10;
    }
}
int main() 
{
