#include<bits/stdc++.h>
using namespace std;
int h(int n){
	if(n==0||n==1) return 1;
	int hh=0;
	for(int i=0;i<n;i++){
		hh+=h(i)*h(n-1-i);//公式
	}
	return hh;
}
