var
    n:longint;
function c(x1,x2:longint):real;
var
    ans:real;
    i:longint;
begin
    ans:=1;
    for i:=x1 downto x1-x2+1 do
        ans:=ans*i;
    for i:=1 to x2 do
        ans:=ans / i;
    exit(ans);
end;
function catalan(x:longint):real;
var
    ans:real;
begin
    ans:=c(2*x,x) / (x+1);//这是卡特兰数的通项公式
    exit(ans);
end;
begin
    readln(n);
    writeln(catalan(n):0:0);
end.
