#include<bits/stdc++.h>
using namespace std;

int a[510];

int main()
{
    int P; cin>>P;

    cout<<ceil(P*log10(2))<<endl; // 第一问

    a[500]=1;
    for(int T=1;T<=P;T++)
    {
        for(int i=500;i>=1;i--)
        {
            a[i]*=2;              // 每一位都乘上2
        }
        for(int i=500;i>=1;i--)
        {
            a[i-1]+=a[i]/10;
            a[i]%=10;             // 处理进位
        }
    }
    a[500]--;

    for(int i=1;i<=500;i++)
    {
        cout<<a[i];
        if(i%50==0) cout<<endl;
    }

    return 0;
}
