			}
			else {
				if(s.divide(BigInteger.TEN.pow(i)).mod(BigInteger.TEN)==BigInteger.ZERO) System.out.print("0");
				else System.out.print(s.divide(BigInteger.TEN.pow(i)).mod(BigInteger.TEN));
			}
		}
	}
}

