void dfs(int t,int v,int n)
{
	if(t>m) {
		ans=max(ans,n);
		return;
	}
	dfs(t+1,v,n);
	if(v-a[t].sj>0) dfs(t+1,v-a[t].sj,n+a[t].jz);
	if(v-a[t].sj==0) {
		ans=max(ans,n+a[t].jz);
		return;
	}
}
