    for(int i=1;i<=M;i++)
    {
        cin>>t[i]>>w[i];
    }
    for(int i=1;i<=M;i++)
    for(int j=1;j<=T;j++)
    {
        if(t[i]<=j)
        {
            dp[i][j]=max(dp[i-1][j],dp[i-1][j-t[i]]+w[i]);//状态转移方程，核心代码
        //    maxn=max(maxn,dp[i][j]);
        }
        else dp[i][j]=dp[i-1][j];//易漏语句，当条件不满足时，dp[i][j]依旧要又上一个状态转移来，否则之后的状态无法转移
    }
    for(int j=1;j<=T;j++)
    maxn=max(maxn,dp[M][j]);
    cout<<maxn;
    return 0;
}
