int w[35];//物品的体积 
int n;
int V;
int dp[20005]; //dp数组 
int main()
{
    scanf("%d %d",&V,&n);
    for(int i=1;i<=n;i++)
    scanf("%d",&w[i]);
    for(int i=1;i<=n;i++)
    for(int j=V;j>=w[i];j--)//没有判定是否超出重量是因为这里枚举已经给了限制 
    {
        dp[j]=max(dp[j],dp[j-w[i]]+w[i]);
     } 
     int ans=0;
     for(int i=1;i<=V;i++)
     ans=max(ans,dp[i]);
     cout<<V-ans<<endl;
     return 0;
}
