#include<bits/stdc++.h>
using namespace std;
int a[31],ans,n;
int solve(int v,int j){			     //v代表箱子目前剩余空间 
	for(int i=j+1;i<=n;i++){
		if(v>=a[i]){
			ans=min(ans,v-a[i]); //记录答案 
			solve(v-a[i],i);     //继续搜索 
		}
	}
}
