	pop = pop * 8 + in - 1;//我们想要的值
	cout<<v - pop;//输出

  下附完整代码

    #include<bits/stdc++.h>
    using namespace std;
    int n, v;
    int a[32];
    int num[3000], tmp[3000];
    int sum;
	int did;
	int ans, out;
	int pop;


	bool mmp(int a, int b)
	{
		return a < b;
	}

	int main()
	{
	//	freopen("1.in", "r", stdin);
	//	freopen("1.out", "w", stdout);
	cin>>v>>n;
	for(int i = 1;i <= n;i++) cin>>a[i];
	sum = v / 8 + 1;//表示占用的槽的数量
	did = (1 << (v % 8 + 1)) - 1;//最后一个占用的槽所要的位数 
	sort(a + 1, a + n + 1, mmp);
	if(a[1] > v)
	{
		cout<<v;
		return 0;
	}
	num[a[1] / 8 + 1] = 1 << (a[1] % 8);
	for(int i = 2;i <= n;i++)
	{
		int j = 0;
		memset(tmp, 0, sizeof(tmp));
		tmp[a[i] / 8 + 1] = 1 << (a[i] % 8);
		int k = a[i] / 8;
		int l = a[i] % 8;
		int lz = (1 << (8 - l)) - 1;
		while(true)
		{
			j++;
		//	cout<<lz<<endl;
			tmp[j + k + 1] |= num[j] >> (8 - l);
			tmp[j + k] |= (num[j] & (lz)) << l;

			if(j + k + 1 > sum) break;
		}
		for(int i = 1;i < sum;i++)
		{
			num[i] |= tmp[i];
		}
		num[sum] = (num[sum] | tmp[sum]) & did;
	}
	for(int i = sum;i >= 1;i--)
	{
		if(num[i] != 0)
		{
			ans = num[i];
			pop = i - 1; 
			break;
		}
	}
	while(ans)
	{
		out = ans & (-ans);
		ans -= out;
	}
	int in = 0;
	while(out)
	{
		in++;
		out /= 2;
	}
	pop = pop * 8 + in - 1;
	cout<<v - pop;
	return 0;
}

