    for(i=1;i<=n;i++)   
    	for(j=vall;j>=0;j--)    //注意：从vall向下搜索，否则会出现a[i]多次累加
    		if(b[j]==1&&j+a[i]<=vall){    //仅需判断加入a[i]的位置是否能得到和加入a[i]后是否超出背包容量
    		b[j+a[i]]=1;    //标记这个空间能得到
    	}
	for(j=vall;j>=0;j--)   //从vall再次向下搜索，直道搜索到第一个有标记过的位置就是能得到的不超过背包容量的最大空间
		if(b[j]==1){  
		markk=j;
		break;
	}
	markk=vall-markk;   //得到剩余空间
