#define MAXN 3000 //本人喜欢用define，代码有点丑，请不要介意
using namespace std;
int v,n;//v是背包容量，n是物品数量
int a[MAXN],Max=-2147483647;//Max用来存最高容量
void dfs(int x,int y)
{
	if(x>v)//如果大于背包容量就返回
	return;
	if(x<=v&&x>Max)//更新Max
	Max=x;
	for(int i=y+1;i<=n;i++)
	dfs(x+a[i],i);//往背包里面装物品
	return;
}
int main()
{
