using namespace std;
const int maxn=1e5+10;
int read() {
    char ch=getchar();int ret=0;
    int neg=1;
    while(ch<'0'||ch>'9') {
        if(ch=='-') neg=-1;
        ch=getchar();
    }
    while(ch>='0'&&ch<='9') ret=ret*10+ch-'0',ch=getchar();
    return ret*neg;
}
int n,a[maxn],m,d[maxn];
int main()
{
    n=read();
    m=read();
    for(int i=1;i<=m;i++) {
        a[i]=read();
    }
    d[0]=1;
    for(int i=1;i<=m;i++) {
        for(int j=n;j>=a[i];j--) {
            d[j]+=d[j-a[i]];//计算方案数
        }
    }
    for(int j=n;j>=0;j--) {
        if(d[j]!=0) {
            printf("%d",n-j);
            break;
        }
    }
    return 0;
}

