      ans:=f;
      name1:=name2;
    end;
    inc(total,f);//累加总奖学金。
  end;
  writeln(name1);//输出最多奖学金人的名字。
  writeln(ans);//输出单人最多奖学金。
  writeln(total);//输出总奖学金。
end.
