```cpp
#include<bits/stdc++.h>//懒人必备万能头 
using namespace std;
int n,m,k,l,d;//五个输入 
int xda[3000],yda[3000];//答案数组 
struct node
{
	int zhou;//计数 
	int t;//用来在排序中不迷失（行号与列号记录） 
};
node x[3000],y[3000];// 两个结构体数组 
bool cmp(node xx,node yy)
{
	return xx.zhou<yy.zhou;
}//定义sort比较方式 
int main()
{
    cin>>n>>m>>k>>l>>d;//输入 
    int kk=k;
    int ll=l;//提前记录用作输出 
    for(int i=1;i<=n;i++)
    {
    	x[i].t=i;
    }
    for(int i=1;i<=m;i++)
    {
    	y[i].t=i;
    }
