{
    cin>>n>>m;
    cin>>h>>s;
    int k;
    cin>>k;
    int x1,x2,y1,y2;
    while(k--)
    {
        cin>>y1>>x1>>y2>>x2;
        if(y1!=y2)
        {
            int x=min(y1,y2);
            cnth[x].c++;
