int talkK[1005];//说话同学横向
int talkL[1005];//说话同学纵向
int findMax(int arr[],int len){//找到最大值
    int index=0,maxV=-2147483648;
    for(int i=0;i<len;i++){
        if(arr[i]>maxV){
            index=i;
            maxV=arr[i];
        }
    }
    return index;
}
int main()
{
    int m,n,k,l,d;
    vector<int> useK,useL;//坑爹的记录数据（最后还要排序
