				for(int k=0;k<=n/2;k++)dp[i][k]=zs[i][k];
			}
		}
	}
	else{
		dp[0][0]=1;
		for(int i=1;i<=m;i++){
			for(int j=0;j<=n/2;j++){
				if(j==n/2)zs[i][j]=dp[i-1][j-1]+dp[i-1][j];
				if(j==0){
					zs[i][0]=dp[i-1][1]*2;
