int n, m, dp[100][100];

int main()
{
	cin >> n >> m;
	for (int i = 1; i <= 100; i++)
		dp[0][i] = 0;
	dp[0][1] = 1;
	for(int i=1;i<=m;i++)
		for (int j = 1; j <= n; j++)
		{
			int left = (j == 1 ? n : j - 1);
			int right = (j == n) ? 1 : j + 1;
			dp[i][j] = dp[i - 1][left] + dp[i - 1][right];
		}
	cout << dp[m][1];
	return 0;
}
