    int *p = (int*)calloc(n,sizeof(int));
    p[0] = 1; // 最开始的主角有1种可能
    for(int t=0;t<m;t++)
    {
        int *temp = (int*)calloc(n,sizeof(int)); // 清零
        for(int i=0;i<n;i++)temp[i] = p[i] , p[i] = 0; // 转移内存
        for(int i=0;i<n;i++)p[i] = temp[(i+1)%n] + temp[(i+n-1)%n]; // 状态转移
