char unit[10][10]={
						"..+---+",
						"./   /|",
						"+---+ |",
						"|   | +",
						"|   |/.",
						"+---+..",};
void plant(int sx,int sy)
{
	for(int i=0;i<6;i++)
		for(int j=0;j<7;j++)
			if(unit[i][j]!='.')
				ans[sx+i][sy+j]=unit[i][j];
}
int main()
{
	memset(ans,'.',sizeof(ans));
	int n,m,w,l=0;
	cin>>m>>n;
	int uper[m][n];
	w=n*4+1+m*2;
	for(int i=0;i<m;i++)
		for(int j=0;j<n;j++)
		{
			cin>>uper[i][j];
			l=max(l,uper[i][j]*3+1+(m-i)*2);
		}
	for(int i=0;i<m;i++)
		for(int j=0;j<n;j++)
			for(int k=1;k<=uper[i][j];k++)
				plant(l-(k*3)-2*(m-i),j*4+1+(m-i-1)*2);
	for(int i=1;i<=l;i++)
	{
		for(int j=1;j<=w;j++)
			cout<<ans[i][j];
		cout<<endl;
	}
	return 0;
}
