```cpp
#include<iostream>
#include<cstdio>
#include<cstdlib>
#include<algorithm>
#include<cstring>
#include<map>
using namespace std;
int n,m,hlen,slen;//宽、长（我把它叫做横长，竖长）
int a[128][128];
char c[1024][1024],s[15][15]={
"        ",
" ..+---+",
" ./   /|",
" +---+ |",
" |   | +",
" |   |/.",
" +---+..",
};//这个是老套路，先把基本的图形绘制下来
void print()//这个是检查用的，十分有用
{
	for(int i=1;i<=slen;i++)
	{
		for(int j=1;j<=hlen;j++)
			printf("%c",c[i][j]);
		printf("\n");
	}
	cout<<endl;
}
void cover(int x,int y,int z)//当前坐标，高度
{
	int sx=slen-((n-x+1)*2+3+1),sy=(n-x)*2+(y-1)*4;
	//******************************************
	//**找出最下面一个块的左上角的左上角的坐标**
    //******************************************
	//不要先看这个，自己推推，不同的人不同的见解
    //只要本质相同（最终求的坐标一样）
    //我来解释下我的吧
    /*
    sx：可以想象一下，如果是在最下面一层，
    它的横坐标不就是
    总共的slen-
    （最靠近我们面前的一层的高度3
    +该行与最面前的一行之间的行数*2
    +自身一个高度1）//为什么要加上自身高度，
	￥￥因为我们求的是最下面一个块的左上角的左上角的坐标
    sy：这个则更为简单看出，即该行距离最面前的一行之间
	还有多少个行 *2，前面已经有了多少列 *4 
    */
    //于是，题目到此解决，重点是细心观察 
    while(z>0)
	{
		for(int i=sx+1;i<=sx+6;i++)
		{
			for(int j=sy+1;j<=sy+7;j++)
			{
				if(s[i-sx][j-sy]=='.') continue;
				c[i][j]=s[i-sx][j-sy];
			}
		}
		z--; sx-=3;
	}
    //print（）；
}
int main()
{
	scanf("%d %d",&n,&m);
	hlen=4*m+2*n+1;
    //我们从最下面一行可以得出这个结论
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=m;j++)
		{
			scanf("%d",&a[i][j]);
			slen=max(slen,3*a[i][j]+2*(n-i+1)+1);
            //我们把每一个位置上的最大高度记下
            //平面上看就是
            //它下面（后面的行）+自身的高度
		}
	}
	/*for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=m;j++)
			cout<<a[i][j]<<" ";
		cout<<endl;
	}*///这个一般不需要，不过我老是犯这个错。。。
	for(int i=1;i<=slen;i++)
	{
		for(int j=1;j<=hlen;j++)
			c[i][j]='.';
	}//先全变成'.';
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=m;j++)
			cover(i,j,a[i][j]);//直接覆盖上去
	}
	for(int i=1;i<=slen;i++)
	{
		for(int j=1;j<=hlen;j++)
			printf("%c",c[i][j]);
		printf("\n");
	}//。。。
	return 0;
}
