void cal_k() {计算基准点的必要条件:最高点
    for(int i=0; i<m; i++) {
        for(int j=0; j<n; j++) {
            k=max(k,2*(m-i-1)+3*(map[i][j]+1)) ;
        }
    }
    return;
}
void cal_l() {
    l = 4*n + 2*m + 1 ;
    return;
}
void cal_std() {
    x0 = k - 4 - 2*m;
    y0 = 2*m - 2;
    return;
}
void paint(int a,int b,int c) { //画第a行第b列从下往上数第c个方块
    int x,y;
    x=x0+2*a-3*c;//计算本块坐标
    y=y0+4*b-2*a;
    for(int i=0; i<6; i++) {
        for(int j=0; j<7; j++) {
            if(block[i][j]!='.') pic[x+i][y+j]=block[i][j];
        }
    }
    return;
}
int main() {
