}
inline void read (int &now)
{
    now = 0;
    char word = getchar ();
    while (word < '0' || word > '9')
        word = getchar ();
    while (word >= '0' && word <= '9')
    {
        now = now * 10 + (int)(word - '0');
        word = getchar ();
    }
}
void Put (int x, int y)  //以下为构建一个立方体 
{
    map[x][y] = map[x][y - 4] = '+';
    for (int i = 1; i <= 3; i++)
        map[x][y - i] = '-';
    map[++x][--y] = map[x][y - 4] = '/';
    map[x][y + 1] = '|';
    for (int i = 1; i <= 3; i++)
        map[x][y - i] = ' ';
    map[++x][--y] = map[x][y - 4] = '+';
    map[x][y + 2] = '|';
    for (int i = 1; i <= 3; i++)
        map[x][y - i] = '-';
    map[x][y + 1] = ' ';
    map[++x][y] = map[x][y - 4] = '|';
    map[x][y + 2] = '+';
    for (int i = 1; i <= 3; i++)
        map[x][y - i] = ' ';
    map[x][y + 1] = ' ';
    map[++x][y] = map[x][y - 4] = '|';
    map[x][y + 1] = '/';
    for (int i = 1; i <= 3; i++)
        map[x][y - i] = ' ';
    map[++x][y] = map[x][y - 4] = '+';
    for (int i = 1; i <= 3; i++)
        map[x][y - i] = '-';
}
int main (int argc, char *argv[])
{
    int N;
    int M;
    read (N);
    read (M);
    int k;
    int X = N * 30;
    int Y = M * 30;
    int x = X, y = Y + 4;
    int up = INF, L = INF;
    int down = -INF, R = -INF;
    for (int i = 1; i <= N; i++)
    {
        for (int j = 1; j <= M; j++)
        {
            read (k);
            x = X;
            y = Y + j * 4;
            down = max (down, x); //  注意！！！ 记录下界要放在操作的上面，我的时间都浪费在了这上面 
            while (k--)
            {
