            }
            R = max (R, y); // 记录其他边界 
            up = min (up, x);
            L = min (L, y);
        }
        X += 2;
        Y -= 2;
    }
    for (int i = up - 1; i <= down + 1; i++)
    {
        for (int j = L; j <= R + 6; j++)
            if (map[i][j] == '|' || map[i][j] == '/' || map[i][j] == '+' || map[i][j] == '-' || map[i][j] == ' ')
                printf ("%c", map[i][j]);
            else    
                printf (".");
        printf ("\n");
    }
    return 0;
}

