```
#include <iostream>

using namespace std;

int num1 = 0;//输入数字个数
int a[10001] = { 0 };//输入的数字 
int b[10001] = { 0 }; //去重后的数字 
int num2 = 0;//输出数字的个数 
int k = 1;//循环变量 

void qsort(int l, int r) {
	int t = a[(l + r) / 2];//目标比较变量
	int i = l, j = r;

	do {
		while (a[i] < t)i++;
		while (a[j] > t)j--;

		if (i <= j) {
			swap(a[i], a[j]);
			i++;
			j--;
		}
	} while (i <= j);
	if (l < j) qsort(l, j);//递归搜索左半部分
	if (i < r) qsort(i, r);//递归搜索右半部分
}

int main() {
	cin >> num1;
	for (int i = 1; i <= num1; i++) {
		cin >> a[i];
	}
	qsort(1, num1);//排序

	for (int j = 1; j <= num1; j++) {
		if (a[j] != a[j + 1]) {
			b[k] = a[j];
			num2++;
			k++;
		}
	}
	cout << num2 << endl;

	for (int i = 1; i <= num2; i++) {
		cout << b[i] << " ";
	}
	return 0;
}
```

