接着上完整代码:

```
#pragma optmize(2)

#include<bits/stdc++.h>

using namespace std;

struct PP{
	int num;
	int pnt;
}a[110];

int n,ans;

inline char nextchar();
inline int read(char type);
inline void write(int x);

bool cmp(PP a,PP b);

int main()
{
	//freopen(".in","r",stdin);
	//freopen(".out","w",stdout);
	ios_base::sync_with_stdio(0);
	cin.tie(NULL);
	n=read('f');
	for(int i=1;i<=n;i++)
	{
		a[i].num=read('f');
	}
	sort(a+1,a+n+1,cmp);

	for(int i=1;i<=n;i++)
	{
		a[i].pnt=i+1;
		if(a[i].num==a[i+1].num)
		{
			int j;
			for(j=i;a[i].num==a[j].num;j++);
			a[i].pnt=j;
		}
		else
		{
			ans++;
		}
	}
	a[n].pnt=-1;

	write(ans);
	puts("");

	int i=1;
	while(i!=-1)
	{
		write(a[i].num);
		putchar(' ');
		i=a[i].pnt;
	}
	return 0;
}

inline char nextchar()
{
	static char buf[100000],*p1=buf, *p2=buf;
	return (p1 == p2)&&(p2=(p1=buf)+fread(buf,1,100000,stdin),p1==p2)?EOF:*p1++;
}

inline int read(char type)
{
	if(type=='f')
	{
		int p=0;
		char ch=0;
		bool sign=false;
		while (!isdigit(ch))
		{
			sign|=(ch=='-');
			ch=nextchar();
		}
		while(isdigit(ch))
		{
			p=p*10+(ch^48);
			ch=nextchar();
		}
		p=sign?-p:p;
		return p;
	}
	else
	{
		int x=0,f=1;
		char ch=getchar();
		while(ch<'0'||ch>'9')
		{
			if(ch=='-')
			{
				f=-1;
			}
			ch=getchar();
		}
		while(ch>='0'&&ch<='9')
		{
			x=(x<<1)+(x<<3)+(ch^48);
			ch=getchar();
		}
		return x*f;
	}
}

inline void write(int x)
{
	static int stk[100],top=0;
	if(x==0)
	{
		putchar('0'); return;
	}
	if(x<0)
	{
		x=-x;
		putchar('-');
	}
	while(x)
	{
		stk[++top]=x%10;
		x/=10;
	}
	while(top)
	{
		putchar(stk[top--]+'0');
	}
}

bool cmp(PP a,PP b){return a.num<b.num;}
```
